function setCookie(e,o,t){let i="";if(t){let e=new Date;e.setTime(e.getTime()+24*t*60*60*1e3),i="; expires="+e.toUTCString()}document.cookie=e+"="+(o||"")+i+"; path=/"}function getCookie(e){let o=document.cookie.match(new RegExp("(?:^|; )"+e.replace(/([\.$?*|{}\(\)\[\]\\\/\+^])/g,"\\$1")+"=([^;]*)"));return o?decodeURIComponent(o[1]):void 0}export function checkCookies(){let e=document.querySelector(".cookie-notification"),o=e.querySelector(".cookie-notification__btn");getCookie("cookies_policy")||(e.style.display="flex"),o.addEventListener("click",(function(){setCookie("cookies_policy","true",1),e.style.display="none"}))}